CREATE TABLE "groups" ("id" integer not null primary key autoincrement, "name" varchar not null, "description" varchar not null, "active" integer not null, "created_at" datetime null, "updated_at" datetime null);
CREATE TABLE `logging` (channel VARCHAR(255), level INTEGER, message LONGTEXT, time INTEGER UNSIGNED);
CREATE TABLE "options" ("id" integer not null primary key autoincrement, "options_group_id" integer not null, "name" varchar not null, "description" varchar not null, "value" varchar null, "type" varchar not null, "code" integer not null, "created_at" datetime null, "updated_at" datetime null, "values" TEXT, "frozen" INTEGER DEFAULT 0);
CREATE TABLE "options_group" ("id" integer not null primary key autoincrement, "name" varchar not null, "description" varchar not null, "active" integer not null, "created_at" datetime null, "updated_at" datetime null);
CREATE TABLE "pages" ("id" integer not null primary key autoincrement, "name" varchar not null, "code" varchar not null, "url_prefix" varchar not null, "category_id" integer not null, "preview_text" text not null, "detail_text" text not null, "preview_picture" varchar not null, "detail_picture" varchar not null, "show_in_menu" integer not null, "name_for_menu" integer not null, "active" integer not null, "created_at" datetime null, "updated_at" datetime null, "slogan" VARCHAR, "fullname" VARCHAR, "sort" INTEGER DEFAULT 100);
CREATE TABLE "sections" ("id" integer not null primary key autoincrement, "name" varchar not null, "code" varchar not null, "sort" varchar not null, "parent_id" varchar not null, "detail_text" text not null, "detail_picture" varchar not null, "show_in_menu" integer not null, "name_for_menu" integer not null, "active" integer not null, "created_at" datetime null, "updated_at" datetime null, "path" VARCHAR);
CREATE TABLE "user_views_settings" ("id" INTEGER PRIMARY KEY  AUTOINCREMENT  NOT NULL  UNIQUE , "user_id" INTEGER NOT NULL , "group" VARCHAR NOT NULL , "option_type" VARCHAR, "code" VARCHAR NOT NULL , "value" VARCHAR NOT NULL );
CREATE TABLE "users" ("id" integer not null primary key autoincrement, "email" varchar not null, "login" varchar not null, "password" varchar not null, "active" integer not null, "created_at" datetime null, "updated_at" datetime null);
